mapper.select:
SELECT vd.`video_id`,vd.`user_id`,vd.`release_type`,vd.`video_title`,vd.`video_description`,vd.`video_picture`,vd.`video_address`, vd.`video_watermark_address`,vd.`job_id`,vd.`is_delete`,vd.`video_date`,vd.`video_likes`, vd.`video_views`,vd.`video_share`,vd.`video_follow`,pt.`latitude`,pt.`longitude`,ui.`image_src`,ui.name userName FROM `video_display` vd LEFT JOIN user_info ui on ui.id = vd.`user_id` LEFT JOIN `positioning` pt ON vd.`user_id` = pt.`user_id` WHERE
vd.`job_id`= #{jobId} and
vd.`release_type` = #{releaseType} and vd.`is_delete`=0 AND vd.`is_hidden`=0 AND vd.`is_violation`=0 ORDER BY vd.`video_date` DESC LIMIT #{pageNow},#{pageSize}

SELECT vd.* FROM `video_display` vd LEFT JOIN user_info ui on ui.id = vd.`user_id` WHERE
vd.`user_id`= #{userId} and
vd.`is_delete`=0 ORDER BY vd.`video_date` DESC LIMIT #{pageNow},#{pageSize}
